package com.mx.totalplay.ffm.cloudweb.utilerias.controller;import com.mx.totalplay.ffm.cloudweb.utilerias.model.ServiceResponseResult;import com.mx.totalplay.ffm.cloudweb.utilerias.service.GenericService;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/req")public class HelpReporteFfmController {    private  final Logger logger = LogManager.getLogger(HelpReporteFfmController.class.getName());    private final GenericService genericService;    @Autowired    public HelpReporteFfmController(GenericService genericService) {        this.genericService = genericService;    }    @PostMapping("/getNombresTablas")    public ResponseEntity<?> getNombresTablas(){        ServiceResponseResult response = genericService.getNombresTablas();        return new ResponseEntity<>(response, HttpStatus.ACCEPTED);    }    @PostMapping("/consultaQuery")    public ResponseEntity<?> consultaQuery(@RequestBody String params){        ServiceResponseResult response = genericService.consultaQuery(params);        return new ResponseEntity<>(response, HttpStatus.ACCEPTED);    }}